import React from "react";

const CountriesInfo = ({ filteredCountries }) => {
    if (filteredCountries.length === 0) {
        return <div>There are no countries to show.</div>;
    }
    if (filteredCountries.length > 10) {
        return <div>Too many matches, specify another filter.</div>;
    }
    if (filteredCountries.length === 1) {
        return (
        <div> 
            <p>{filteredCountries.name}</p>
            <p>Capital: {filteredCountries.capital}</p>
            <p>Population: {filteredCountries.population}</p>
            <h2>Languages </h2>
            {/* show filterCountries languages object all key-value pairs */}
            <ul>
                {filteredCountries.languages.entries.array.forEach(element => {
                    <li>{element}</li>
                console.log(filteredCountries);
                })}
            </ul>
            <img src={filteredCountries.flag} alt="flag" width="100" />
        </div>
        );
    }

    if (filteredCountries.length > 1 && filteredCountries.length <= 10) {
    return (
        <div>
        <h1>Country Data</h1>
        <ul>
            {filteredCountries.map((country) => (
            <li key={country.name.common}>
                {country.name.common}
            </li>
            ))}
        </ul>
        </div>
    );
}
    };

export default CountriesInfo;
