import axios from 'axios'
import React, { useState, useEffect } from 'react'

const App = () => {
  //get all data from http://localhost:3004/persons by using axios
  const [persons, setPersons] = useState([])

  useEffect(() => {
    axios
      .get('http://localhost:3004/persons')
      .then(response => {
        setPersons(response.data)
      })
  }, [])

  //add new person to http://localhost:3004/persons by using axios
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const addPerson = (event) => {
    event.preventDefault()
    const newPerson= {
      name: newName,
      number: newNumber
    }
    axios
      .post('http://localhost:3004/persons', newPerson)
      .then(response => {
        setPersons(persons.concat(response.data))
        setNewName('')
        setNewNumber('')
      })
  }

  //delete person from http://localhost:3004/persons by using axios, 
  const deletePerson = (id) => {
    axios
      .delete(`http://localhost:3004/persons/${id}`)
      .then(response => {
        setPersons(persons.filter(person => person.id !== id))
      })
  }


  return (
    <div>
      <h2>Phonebook</h2>

      <div>
        <form>
          <div>
            filter shown with <input />
          </div>
        </form>
      </div>

      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={(event) => setNewName(event.target.value)} />
        </div>
        <div>
          number: <input value={newNumber} onChange={(event) => setNewNumber(event.target.value)} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>

      </form>

      <h2>Numbers</h2>
      <div>
        {persons.map(person => <p key={person.name}>{person.name} {person.number}
        <button onClick={() => deletePerson(person.id)}>delete</button>
        </p>)}
      </div>
    </div>
  )
}

export default App
