import axios from 'axios'
import React, { useState, useEffect } from 'react'

const App = () => {
  //get all data from http://localhost:3004/persons by using axios
  const [persons, setPersons] = useState([])
  useEffect(() => {
    axios
      .get('http://localhost:3004/persons')
      .then(response => {
        setPersons(response.data)
      })
  }, [])

  //add new person to http://localhost:3004/persons by using axios
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const addPerson = (event) => {
    event.preventDefault()
    const newPerson= {
      name: newName,
      number: newNumber
    }
    axios
      .post('http://localhost:3004/persons', newPerson)
      .then(response => {
        setPersons(persons.concat(response.data))
        setNewName('')
        setNewNumber('')
      })
  }


  return (
    <div>
      <h2>Phonebook</h2>

      <div>
        <form>
          <div>
            filter shown with <input />
          </div>
        </form>
      </div>

      <form onSubmit={addPerson}>
        <div>name: <input va/></div>
        <div>number: <input /></div>
        <div><button type="submit">add</button></div>
      </form>

      <h2>Numbers</h2>
      <div>
        {persons.map(person => <p key={person.name}>{person.name} {person.number}</p>)}
      </div>
    </div>
  )
}

export default App
